#include <stdio.h>

int main() {
    int vetor[5];
    int i;

    printf("Digite 5 numeros inteiros:\n");
    for (i = 0; i < 5; i++) {
        scanf("%d", &vetor[i]);
    }

    printf("Ordem inversa: ");
    for (i = 4; i >= 0; i--) {
        printf("%d ", vetor[i]);
    }
    printf("\n");

    return 0;
}

#include <stdio.h>

int main() {
    float vetor[10];
    int i, posicaomaior = 0;
    float maior;

    for(i = 0; i < 10; i++) {
        scanf("%f", &vetor[i]);
    }

    maior = vetor[0];

    for(i = 1; i < 10; i++) {
        if (vetor[i] > maior) {
            maior = vetor[i];
            posicaomaior = i;
        }
    }

    printf("O maior elemento e %.2f e esta na posicao %d.\n", maior, posicaomaior);

    return 0;
}

#include <stdio.h>

int main() {
    float notas[20], soma = 0.0, media;
    int n, i;

    printf("Quantos alunos na turma (max 20)? ");
    scanf("%d", &n);

    printf("Digite as %d notas:\n", n);
    for(i = 0; i < n; i++) {
        scanf("%f", &notas[i]);
    }

    for(i = 0; i < n; i++) {
        soma += notas[i];
    }

    media = soma / n;

    printf("Soma das notas: %.2f\n", soma);
    printf("Media da turma: %.2f\n", media);

    return 0;
}
